#include <filesystem>#include <fstream>#include <iostream>#include <unordered_set>#include <regex>namespace fs = std::filesystem;std::vector<std::string> collectAllTextFiles(const std::string& rootDirectory) {    std::vector<std::string> textFiles;    for (const auto& entry : fs::recursive_directory_iterator(rootDirectory)) {        if (entry.is_regular_file() && entry.path().extension() == ".txt") {            textFiles.push_back(fs::absolute(entry.path()).string());        }    }    return textFiles;}std::string loadFileContent(const std::string& filePath) {    std::ifstream fileStream(filePath);    if (!fileStream.is_open()) {        std::cerr << "Failed to open file: " << filePath << std::endl;        return "";    }    std::string content((std::istreambuf_iterator<char>(fileStream)), std::istreambuf_iterator<char>());    return content;}std::vector<std::string> parseRequiredFiles(const std::string& fileContent) {    std::vector<std::string> dependencies;    std::regex requireRegex(R"(require\s*['"]([^'"]+)['"])");    std::smatch matches;    std::string::const_iterator searchStart(fileContent.cbegin());    while (std::regex_search(searchStart, fileContent.cend(), matches, requireRegex)) {        dependencies.push_back(matches[1]);        searchStart = matches.suffix().first;    }    return dependencies;}